    private void input1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_input1ActionPerformed
        // TODO add your handling code here:
       //From fahrenheit to celcius multiply subract 32 and multiply 0.55 to get the answer
        String Output1 = output1.getText();

        double Output1s = Double.parseDouble(Output1);

        double fahcels = (Output1s - 32) * 0.55;

        output2.setText("" + fahcels);

        output1.setText("");
    }//GEN-LAST:event_input1ActionPerformed

