    private void input2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_input2ActionPerformed
        // TODO add your handling code here:
        //From celcius to fahrenheit multiply 1.8 and add 32 to get the answer 
        String Output2 = output2.getText();

        double Output2s = Double.parseDouble(Output2);

        double celsfah = (Output2s * 1.8) + 32;

        output1.setText("" + celsfah);

        output2.setText("");

    }//GEN-LAST:event_input2ActionPerformed

